select TRANSFORM(logevents.uuid,logevents.c_6,logevents.c_7) USING 'hive2csv.py' AS dummy from (select logevents.uuid,MIN(logevents.c_7) as c_7,MIN(logevents.c_6) as c_6 from logevents where logevents.ds>='#STARTDATE' and logevents.ds<='#ENDDATE' and logevents.server_date>='#STARTDATE #STARTTIME' and logevents.server_date<='#ENDDATE #ENDTIME' group by logevents.uuid) logevents group by logevents.uuid,logevents.c_6,logevents.c_7